from accessify import privateimport csvclass Item:    """    Класс для представления товара в магазине.    """    pay_rate = 1.0    all = []    def __init__(self, name: str, price: float, quantity: int) -> None:        """        Создание экземпляра класса item.        :param name: Название товара.        :param price: Цена за единицу товара.        :param quantity: Количество товара в магазине.        """        self.price = price        self.quantity=quantity        Item.all.append(self)        @private        def name(self):            self.name = name    @property    def get_name(self):        return self.name    @get_name.setter    def set_name(self, name):        if len(name ) <=10:            self.name =name        else:            self.name = name[:10]    def calculate_total_price(self) -> float:        """        Рассчитывает общую стоимость конкретного товара в магазине.        :return: Общая стоимость товара.        """        return self.price * self.quantity    def apply_discount(self) -> float:        """        Применяет установленную скидку для конкретного товара.        """        return self.price * Item.pay_rate    @classmethod    def instantiate_from_csv(cls, name_csvfile):        with open(name_csvfile) as csvfile:            csv_dict = csv.DictReader(csvfile)            list_obj = []            for el in csv_dict:                item = Item(el['name'], el['price'], el['quantity'])                list_obj.append(item)            return list_obj    @staticmethod    def string_to_number(num):        new_num=round(float(num ))        return new_num